options.customCss:
:root {
  /* Base Colors */
  --chat--color-primary: #e74266;
  --chat--color-primary-shade-50: #db4061;
  --chat--color-primary-shade-100: #cf3c5c;
  --chat--color-secondary: #20b69e;
  --chat--color-white: #ffffff;
  --chat--color-light: #f2f4f8;
  --chat--color-dark: #101330;

  /* Typography */
  --chat--font-family: 'Segoe UI', Roboto, sans-serif;

  /* Layout */
  --chat--spacing: 1rem;
  --chat--border-radius: 0.75rem;
  --chat--transition-duration: 0.2s;

  /* Chat Window */
  --chat--window--width: 420px;
  --chat--window--height: 600px;
  --chat--window--bottom: 2rem;
  --chat--window--right: 2rem;
  --chat--window--border-radius: 1rem;
  --chat--window--border: 1px solid #e0e0e0;

  /* Input Area */
  --chat--textarea--height: 44px;
  --chat--textarea--max-height: 120px;
  --chat--input--font-size: 0.95rem;
  --chat--input--padding: 0.5rem 0.75rem;
  --chat--input--border-radius: 1.25rem;
  --chat--input--background: #ffffff;
  --chat--input--text-color: #101330;
  --chat--input--border-active: 2px solid var(--chat--color-primary);

  /* Message Style */
  --chat--message--font-size: 1rem;
  --chat--message--padding: 0.85rem;
  --chat--message--border-radius: 1rem;
  --chat--message--user--background: var(--chat--color-primary);
  --chat--message--user--color: var(--chat--color-white);
  --chat--message--bot--background: #ffffff;
  --chat--message--bot--color: var(--chat--color-dark);
  --chat--message--margin-bottom: 0.8rem;

  /* Send Button */
  --chat--input--send--button--background: var(--chat--color-primary);
  --chat--input--send--button--color: var(--chat--color-white);
  --chat--input--send--button--background-hover: var(--chat--color-primary-shade-50);
  --chat--input--send--button--color-hover: var(--chat--color-white);

  /* Files, Buttons, etc. */
  --chat--files-spacing: 0.5rem;
}

/* Custom prompt alignment - centered and beautiful */
.chat-input-wrapper {
  display: flex !important;
  justify-content: center !important;
  align-items: center !important;
  padding: 0.75rem 1.5rem !important;
  background-color: #fff0f4; /* subtle warm background */
  border-radius: 2rem !important;
  box-shadow: 0 8px 24px rgba(231, 66, 102, 0.15);
  max-width: 720px;
  margin: 0 auto;
}

/* Input styling */
.chat-input {
  max-width: 70% !important;
  width: 100%;
  font-size: 1.1rem !important;
  border-radius: 2.5rem !important; /* softer pill shape */
  padding: 0.8rem 1.5rem !important;
  background-color: #ffffff !important;
  border: 2.5px solid var(--chat--color-primary) !important;
  box-shadow:
    0 4px 12px rgba(231, 66, 102, 0.12),
    inset 0 1px 3px rgba(255, 255, 255, 0.9);
  transition: border-color 0.25s ease, box-shadow 0.25s ease;
  font-weight: 600;
  color: var(--chat--color-dark);
  font-family: var(--chat--font-family);
}

/* Focus state with bright highlight */
.chat-input:focus {
  outline: none !important;
  border-color: var(--chat--color-primary-shade-100) !important;
  box-shadow:
    0 0 12px 3px rgba(231, 66, 102, 0.35),
    inset 0 1px 5px rgba(255, 255, 255, 0.95);
  background-color: #fff7fa !important;
}

/* Optional: Style for send button if used */
.chat-input-send-button {
  background-color: var(--chat--color-primary);
  color: var(--chat--color-white);
  border: none;
  border-radius: 2rem;
  padding: 0.7rem 1.5rem;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.3s ease;
  margin-left: 1rem;
  box-shadow: 0 4px 12px rgba(231, 66, 102, 0.3);
}

.chat-input-send-button:hover {
  background-color: var(--chat--color-primary-shade-50);
}

/* Messages styling (optional tweak) */
.chat-message {
  max-width: 60%;
  font-size: var(--chat--message--font-size);
  padding: var(--chat--message--padding);
  border-radius: var(--chat--message--border-radius);
  margin-bottom: var(--chat--message--margin-bottom);
  line-height: 1.4;
  word-wrap: break-word;
}

/* User message style */
.chat-message.user {
  background-color: var(--chat--message--user--background);
  color: var(--chat--message--user--color);
  align-self: flex-end;
}

/* Bot message style */
.chat-message.bot {
  background-color: var(--chat--message--bot--background);
  color: var(--chat--message--bot--color);
  align-self: flex-start;
}
