parameters.inputSchema:
{
  "name": "resume_screening_evaluation",
  "description": "Analyzes a candidate's resume against a job description and outputs strengths, weaknesses, risk/reward assessment, and an overall fit score.",
  "type": "object",
  "properties": {
    "candidate_strengths": {
      "type": "array",
      "description": "A list of specific strengths or qualifications that match the job description.",
      "items": {
        "type": "string"
      }
    },
    "candidate_weaknesses": {
      "type": "array",
      "description": "A list of areas where the candidate falls short or lacks alignment with the job requirements.",
      "items": {
        "type": "string"
      }
    },
    "risk_factor": {
      "type": "object",
      "description": "An evaluation of the potential risks of hiring this candidate.",
      "properties": {
        "score": {
          "type": "string",
          "enum": ["Low", "Medium", "High"],
          "description": "The risk level of hiring this candidate."
        },
        "explanation": {
          "type": "string",
          "description": "A brief explanation of the worst-case scenario if the candidate is hired."
        }
      },
      "required": ["score", "explanation"]
    },
    "reward_factor": {
      "type": "object",
      "description": "An evaluation of the potential upside of hiring this candidate.",
      "properties": {
        "score": {
          "type": "string",
          "enum": ["Low", "Medium", "High"],
          "description": "The reward level of hiring this candidate."
        },
        "explanation": {
          "type": "string",
          "description": "A brief explanation of the best-case scenario if the candidate is hired."
        }
      },
      "required": ["score", "explanation"]
    },
    "overall_fit_rating": {
      "type": "integer",
      "description": "A numerical score (0-10) representing the overall fit of the candidate for the role."
    },
    "justification_for_rating": {
      "type": "string",
      "description": "A detailed explanation of the rationale behind the overall fit rating."
    }
  }
}
